option.value:
using System;
using System.Collections.Generic;
using System.Linq;
using UnityEngine;

namespace League
{
    public static class AIRaceSimulator
    {
        private static System.Random random = new System.Random();

        /// <summary>
        /// Simulates a race between AI teams and returns the finishing positions.
        /// Teams with higher quality + form have better chances of winning.
        /// </summary>
        /// <param name="teams">Teams participating in the race</param>
        /// <returns>Dictionary with team and their finishing position</returns>
        public static Dictionary<Team, int> SimulateRace(Team[] teams)
        {
            if (teams == null || teams.Length == 0)
                return new Dictionary<Team, int>();

            var teamPerformances = new List<TeamRacePerformance>();

            // Calculate race performance for each team with some randomness
            foreach (var team in teams)
            {
                float basePerformance = team.GetRacePerformance();

                // Add randomness (±15 points) to make races unpredictable
                float randomVariation = (float)(random.NextDouble() * 30 - 15);
                float finalPerformance = basePerformance + randomVariation;

                // Add small boost for teams currently in good league position (championship pressure)
                float positionBonus = GetPositionBonus(team);
                finalPerformance += positionBonus;

                teamPerformances.Add(new TeamRacePerformance
                {
                    team = team,
                    performance = finalPerformance
                });
            }

            // Sort by performance (highest first = best finishing position)
            teamPerformances = teamPerformances
                .OrderByDescending(tp => tp.performance)
                .ToList();

            // Assign finishing positions and record results
            var results = new Dictionary<Team, int>();
            for (int i = 0; i < teamPerformances.Count; i++)
            {
                int position = i + 1;
                var team = teamPerformances[i].team;

                results[team] = position;
                team.RecordRaceFinish(position);
            }

            return results;
        }

        /// <summary>
        /// Gives teams currently higher in standings a small performance boost
        /// to help maintain competitive balance.
        /// </summary>
        private static float GetPositionBonus(Team team)
        {
            var league = LeagueController.Instance?.currentLeague;
            if (league?.standings == null) return 0f;

            var standing = league.standings.FirstOrDefault(s => s.team == team);
            if (standing.team == null) return 0f;

            // Top 3 teams get small bonus, bottom teams get slight penalty
            int totalTeams = league.standings.Length;
            if (standing.position <= 3)
                return 2f; // Small boost for leaders
            else if (standing.position >= totalTeams - 2)
                return -1f; // Small penalty for bottom teams

            return 0f; // No bonus for mid-table teams
        }

        /// <summary>
        /// Simulates all non-player races for the current race week.
        /// Call this when advancing the league to the next race.
        /// </summary>
        public static void SimulateWeeklyAIRaces(League league)
        {
            if (league?.raceDays == null || league.currentRace >= league.raceDays.Length)
                return;

            // Simulate ALL AI-only races for this week, not just the current race
            SimulateAllAIRacesForWeek(league);
        }

        /// <summary>
        /// Simulates all AI-only races that should happen during the same week as the player's race.
        /// This ensures AI teams that aren't racing the player still compete.
        /// </summary>
        public static void SimulateAllAIRacesForWeek(League league)
        {
            if (league?.raceDays == null || league.currentRace >= league.raceDays.Length)
                return;

            // Get all teams in the league
            var allTeams = league.teams.ToList();
            var playerTeam = allTeams.FirstOrDefault(t => t.teamType == TeamType.Player);

            if (playerTeam == null) return;

            // Get current player race
            var currentRaceDay = league.raceDays[league.currentRace];
            var teamsInPlayerRace = currentRaceDay.teams?.ToList() ?? new List<Team>();

            // Find teams NOT in the player's race
            var teamsNotRacing = allTeams.Where(t => !teamsInPlayerRace.Contains(t) && t != playerTeam).ToList();

            // If we have enough teams not racing, create AI-only races
            if (teamsNotRacing.Count >= 2)
            {
                // Create AI-only races from remaining teams
                var aiRaces = CreateAIOnlyRaces(teamsNotRacing, league.maxNumberOfBoatsPerRace);

                foreach (var aiRace in aiRaces)
                {
                    if (aiRace.Length >= 2) // Need at least 2 teams to race
                    {
                        var results = SimulateRace(aiRace);
                        Debug.Log($"Simulated AI-only race: {string.Join(", ", results.Select(r => $"{r.Key.teamName}: {r.Value}"))}");
                    }
                }
            }

            // Also simulate the current race if it's AI-only
            var currentRaceDay2 = league.raceDays[league.currentRace];
            if (currentRaceDay2.teams != null)
            {
                bool playerInRace = currentRaceDay2.teams.Any(t => t.teamType == TeamType.Player);

                if (!playerInRace)
                {
                    // Simulate this AI-only race
                    var results = SimulateRace(currentRaceDay2.teams);

                    // Update the race day with results
                    var positions = new int[currentRaceDay2.teams.Length];
                    for (int i = 0; i < currentRaceDay2.teams.Length; i++)
                    {
                        if (results.ContainsKey(currentRaceDay2.teams[i]))
                            positions[i] = results[currentRaceDay2.teams[i]];
                    }
                    currentRaceDay2.positions = positions;
                    league.raceDays[league.currentRace] = currentRaceDay2;

                    Debug.Log($"Simulated scheduled AI race - Results: {string.Join(", ", results.Select(r => $"{r.Key.teamName}: {r.Value}"))}");
                }
            }
        }

        /// <summary>
        /// Creates AI-only races from teams that aren't racing against the player this week.
        /// </summary>
        private static List<Team[]> CreateAIOnlyRaces(List<Team> availableTeams, int maxBoatsPerRace)
        {
            var aiRaces = new List<Team[]>();
            var remainingTeams = new List<Team>(availableTeams);

            // Create races with available teams
            while (remainingTeams.Count >= 2)
            {
                int raceSize = Mathf.Min(maxBoatsPerRace, remainingTeams.Count);

                // Take teams for this race
                var raceTeams = remainingTeams.Take(raceSize).ToArray();
                aiRaces.Add(raceTeams);

                // Remove teams from remaining pool
                foreach (var team in raceTeams)
                {
                    remainingTeams.Remove(team);
                }
            }

            return aiRaces;
        }

        private struct TeamRacePerformance
        {
            public Team team;
            public float performance;
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using UnityEngine;

namespace League
{
    public static class AIRaceSimulator
    {
        private static System.Random random = new System.Random();

        /// <summary>
        /// Simulates a race between AI teams and returns the finishing positions.
        /// Teams with higher quality + form have better chances of winning.
        /// </summary>
        /// <param name="teams">Teams participating in the race</param>
        /// <returns>Dictionary with team and their finishing position</returns>
        public static Dictionary<Team, int> SimulateRace(Team[] teams)
        {
            if (teams == null || teams.Length == 0)
                return new Dictionary<Team, int>();

            var teamPerformances = new List<TeamRacePerformance>();

            // Calculate race performance for each team with some randomness
            foreach (var team in teams)
            {
                float basePerformance = team.GetRacePerformance();

                // Add randomness (±15 points) to make races unpredictable
                float randomVariation = (float)(random.NextDouble() * 30 - 15);
                float finalPerformance = basePerformance + randomVariation;

                // Add small boost for teams currently in good league position (championship pressure)
                float positionBonus = GetPositionBonus(team);
                finalPerformance += positionBonus;

                teamPerformances.Add(new TeamRacePerformance
                {
                    team = team,
                    performance = finalPerformance
                });
            }

            // Sort by performance (highest first = best finishing position)
            teamPerformances = teamPerformances
                .OrderByDescending(tp => tp.performance)
                .ToList();

            // Assign finishing positions and record results
            var results = new Dictionary<Team, int>();
            for (int i = 0; i < teamPerformances.Count; i++)
            {
                int position = i + 1;
                var team = teamPerformances[i].team;

                results[team] = position;
                team.RecordRaceFinish(position);
            }

            return results;
        }

        /// <summary>
        /// Gives teams currently higher in standings a small performance boost
        /// to help maintain competitive balance.
        /// </summary>
        private static float GetPositionBonus(Team team)
        {
            var league = LeagueController.Instance?.currentLeague;
            if (league?.standings == null) return 0f;

            var standing = league.standings.FirstOrDefault(s => s.team == team);
            if (standing.team == null) return 0f;

            // Top 3 teams get small bonus, bottom teams get slight penalty
            int totalTeams = league.standings.Length;
            if (standing.position <= 3)
                return 2f; // Small boost for leaders
            else if (standing.position >= totalTeams - 2)
                return -1f; // Small penalty for bottom teams

            return 0f; // No bonus for mid-table teams
        }

        /// <summary>
        /// Simulates all non-player races for the current race week.
        /// Call this when advancing the league to the next race.
        /// </summary>
        public static void SimulateWeeklyAIRaces(League league)
        {
            if (league?.raceDays == null || league.currentRace >= league.raceDays.Length)
                return;

            // Simulate ALL AI-only races for this week, not just the current race
            SimulateAllAIRacesForWeek(league);
        }

        /// <summary>
        /// Simulates all AI-only races that should happen during the same week as the player's race.
        /// This ensures AI teams that aren't racing the player still compete in their own heats.
        /// Each team races exactly once per race button press.
        /// </summary>
        public static void SimulateAllAIRacesForWeek(League league)
        {
            if (league?.raceDays == null || league.currentRace >= league.raceDays.Length)
                return;

            // Get all teams in the league
            var allTeams = league.teams.ToList();
            var playerTeam = allTeams.FirstOrDefault(t => t.teamType == TeamType.Player);

            if (playerTeam == null) return;

            // Get current player race
            var currentRaceDay = league.raceDays[league.currentRace];
            var teamsInPlayerRace = currentRaceDay.teams?.ToList() ?? new List<Team>();

            // Find teams NOT in the player's race - these need to race in their own heats
            var teamsNotRacing = allTeams.Where(t => !teamsInPlayerRace.Contains(t) && t != playerTeam).ToList();

            // Create exactly ONE heat for the remaining teams
            if (teamsNotRacing.Count >= 2)
            {
                // Create a single AI-only race with all remaining teams (up to max boats per race)
                int raceSize = Mathf.Min(league.maxNumberOfBoatsPerRace, teamsNotRacing.Count);
                var raceTeams = teamsNotRacing.Take(raceSize).ToArray();

                if (raceTeams.Length >= 2) // Need at least 2 teams to race
                {
                    var results = SimulateRace(raceTeams);
                    Debug.Log($"Simulated AI heat: {string.Join(", ", results.Select(r => $"{r.Key.teamName}: {r.Value}"))}");

                    // Log points awarded to verify max 25 points
                    foreach (var result in results)
                    {
                        int points = CalculatePointsForPosition(result.Value, raceTeams.Length);
                        Debug.Log($"{result.Key.teamName} earned {points} points for position {result.Value}");
                    }
                }

                // If there are still teams left over (more than max boats per race), they don't race this week
                if (teamsNotRacing.Count > league.maxNumberOfBoatsPerRace)
                {
                    var leftOverTeams = teamsNotRacing.Skip(raceSize).ToList();
                    Debug.Log($"Teams sitting out this week: {string.Join(", ", leftOverTeams.Select(t => t.teamName))}");
                }
            }

            // Handle scheduled AI-only races (if any exist in the race days)
            var currentRaceDay2 = league.raceDays[league.currentRace];
            if (currentRaceDay2.teams != null)
            {
                bool playerInRace = currentRaceDay2.teams.Any(t => t.teamType == TeamType.Player);

                if (!playerInRace)
                {
                    // This is a scheduled AI-only race - simulate it
                    var results = SimulateRace(currentRaceDay2.teams);

                    // Update the race day with results
                    var positions = new int[currentRaceDay2.teams.Length];
                    for (int i = 0; i < currentRaceDay2.teams.Length; i++)
                    {
                        if (results.ContainsKey(currentRaceDay2.teams[i]))
                            positions[i] = results[currentRaceDay2.teams[i]];
                    }
                    currentRaceDay2.positions = positions;
                    league.raceDays[league.currentRace] = currentRaceDay2;

                    Debug.Log($"Simulated scheduled AI race - Results: {string.Join(", ", results.Select(r => $"{r.Key.teamName}: {r.Value}"))}");
                }
            }
        }

        /// <summary>
        /// Calculates points for a finishing position (F1-style scoring).
        /// 1st = 25, 2nd = 18, 3rd = 15, 4th = 12, etc.
        /// </summary>
        private static int CalculatePointsForPosition(int position, int totalRacers)
        {
            return position switch
            {
                1 => 25,
                2 => 18,
                3 => 15,
                4 => 12,
                5 => 10,
                6 => 8,
                7 => 6,
                8 => 4,
                9 => 2,
                10 => 1,
                _ => 0
            };
        }

        private struct TeamRacePerformance
        {
            public Team team;
            public float performance;
        }
    }
}

using UnityEngine;
using UnityEditor;
using League;

[CustomEditor(typeof(LeagueController))]
public class LeagueControllerEditor : Editor
{
    public override void OnInspectorGUI()
    {
        // Draw the default inspector
        DrawDefaultInspector();

        GUILayout.Space(10);

        LeagueController leagueController = (LeagueController)target;

        // Add a visible button to clear the league
        if (GUILayout.Button("Clear League", GUILayout.Height(30)))
        {
            if (EditorUtility.DisplayDialog("Clear League", 
                "Are you sure you want to clear the current league? This will reset all race data and team stats.", 
                "Yes", "Cancel"))
            {
                leagueController.ClearLeague();
            }
        }

        GUILayout.Space(5);

        // Add some helpful info
        if (leagueController.currentLeague != null)
        {
            EditorGUILayout.HelpBox($"Current League: {leagueController.currentLeague.leagueName}\n" +
                                  $"Current Race: {leagueController.currentLeague.currentRace + 1}\n" +
                                  $"Total Races: {leagueController.currentLeague.raceDays?.Length ?? 0}\n" +
                                  $"Season Finished: {leagueController.currentLeague.isFinished}", 
                                  MessageType.Info);
        }
        else
        {
            EditorGUILayout.HelpBox("No current league assigned!", MessageType.Warning);
        }
    }
}

using System.Collections.Generic;
using UnityEngine;
using System.Linq;

namespace League
{
    public class LeagueController : MonoBehaviour
    {
        public static LeagueController Instance { get; private set; }
        public League currentLeague;
        public League[] leagues;


        private void Awake()
        {
            if (Instance == null)
            {
                Instance = this;
                DontDestroyOnLoad(gameObject);
            }
            else
            {
                Destroy(gameObject);
            }
        }


        private void Start()
        {
            // Generate raw schedule and enforce repeat and limit
            var rawSchedule = currentLeague.GenerateRaceSchedule(currentLeague.teams, currentLeague.maxNumberOfBoatsPerRace, currentLeague.repeatCount).ToList();
            if (rawSchedule.Count > currentLeague.maxRaces)
                rawSchedule = rawSchedule.Take(currentLeague.maxRaces).ToList();
            // Initialize race days array
            currentLeague.raceDays = new RaceDayFormation[rawSchedule.Count];
            for (int i = 0; i < rawSchedule.Count; i++)
            {
                currentLeague.raceDays[i] = new RaceDayFormation
                {
                    teams = rawSchedule[i],
                    positions = new int[rawSchedule[i].Length]
                };
            }
        }

        /// <summary>
        /// Advances to the next race in the league schedule.
        /// Automatically simulates AI-only races.
        /// </summary>
        public void AdvanceToNextRace()
        {
            if (currentLeague == null || currentLeague.raceDays == null) return;

            // Simulate current race if it's AI-only
            AIRaceSimulator.SimulateWeeklyAIRaces(currentLeague);

            // Move to next race
            currentLeague.currentRace++;

            // Check if season is complete
            if (currentLeague.currentRace >= currentLeague.raceDays.Length)
            {
                CompleteSeasonInternal();
                return;
            }

            // Update standings after race results
            currentLeague.RecalculateStandings();

            // Simulate next race if it's also AI-only (for multiple AI races per week)
            AIRaceSimulator.SimulateWeeklyAIRaces(currentLeague);
        }

        /// <summary>
        /// Gets the current race that the player should participate in.
        /// Returns null if no player races remaining.
        /// </summary>
        public RaceDayFormation? GetCurrentPlayerRace()
        {
            if (currentLeague?.raceDays == null) return null;

            // Look for next race containing the player
            for (int i = currentLeague.currentRace; i < currentLeague.raceDays.Length; i++)
            {
                var raceDay = currentLeague.raceDays[i];
                if (raceDay.teams?.Any(t => t.teamType == TeamType.Player) == true)
                {
                    return raceDay;
                }
            }

            return null;
        }

        /// <summary>
        /// Records the player's race result and advances the league.
        /// </summary>
        public void RecordPlayerRaceResult(int playerPosition, Team[] raceTeams, int[] allPositions)
        {
            if (currentLeague?.raceDays == null) return;

            // Find and update the current race day
            var currentRaceDay = currentLeague.raceDays[currentLeague.currentRace];
            currentRaceDay.positions = allPositions;
            currentLeague.raceDays[currentLeague.currentRace] = currentRaceDay;

            // Record results for all teams in this race
            for (int i = 0; i < raceTeams.Length && i < allPositions.Length; i++)
            {
                raceTeams[i].RecordRaceFinish(allPositions[i]);
            }

            // Advance to next race
            AdvanceToNextRace();
        }

        private void CompleteSeasonInternal()
        {
            currentLeague.isFinished = true;
            currentLeague.RecalculateStandings();
            Debug.Log($"Season {currentLeague.currentSeason} completed for {currentLeague.leagueName}!");

            // TODO: Handle promotion/relegation, start new season, etc.
        }

        /// <summary>
        /// Clears the current league and resets all race data.
        /// This method can be called from the inspector button.
        /// </summary>
        [ContextMenu("Clear League")]
        public void ClearLeague()
        {
            if (currentLeague != null)
            {
                currentLeague.currentRace = 0;
                currentLeague.isFinished = false;
                currentLeague.raceDays = null;
                currentLeague.standings = null;

                // Reset all team stats
                if (currentLeague.teams != null)
                {
                    foreach (var team in currentLeague.teams)
                    {
                        if (team != null)
                        {
                            team.ResetCurrentSeasonStats();
                        }
                    }
                }

                Debug.Log($"League '{currentLeague.leagueName}' has been cleared and reset!");
            }
            else
            {
                Debug.LogWarning("No current league to clear!");
            }
        }

        /// <summary>
        /// Regenerates the race schedule for the current league.
        /// Useful for testing different race combinations.
        /// </summary>
        [ContextMenu("Regenerate Race Schedule")]
        public void RegenerateRaceSchedule()
        {
            if (currentLeague != null)
            {
                Start(); // Calls the race generation logic
                Debug.Log($"Race schedule regenerated for '{currentLeague.leagueName}'!");
            }
            else
            {
                Debug.LogWarning("No current league to regenerate schedule for!");
            }
        }
    }
}

