    [Header("Form & Performance")]
    [Tooltip("Current form rating (0-100). Higher form = better recent performance.")]
    [Range(0, 100)]
    public float currentForm = 50f;
    [Tooltip("Recent race results used to calculate form (last 5 races).")]
